method.section:

	p = new PageHandel(request, hql.toString(), conditions);
	p = this.getPageHandel(p, hqlColumnName, headerName);
	p.setStyle("$[style]");
	p.setExecuteModel(PageHandel.EXE_OBJECT_MODEL);
	p.execute();


	hql.append(" where 1=1 ");
	if (!StringUtil.isNullOrBlank($[condition])) {
		hql.append(" and $[condition] = :$[condition] ");
		conditions.add("$[condition]", $[condition]);
	}


	List<Object[]> payErrorReturnCodeList = p.getPage().getData();
	StringBuffer xml = new StringBuffer();
	int NO = p.getPage().getFirstRecord() + 1;
	xml.append("<rows>");
	for (Object[] objects : payErrorReturnCodeList) {
		Object id = objects[0];
		objects[0] = NO;
		xml.append(this.reformXml(objects, id));
		NO++;
	}
	xml.append("</rows>");


	if (StringUtil.isNullOrBlank(HttpUtil.getParamValueAllowNull(
			request, "flag"))) {
		this.write(response, p.getPage().getTotalRecord() + "[split]"
				+ p.getPage().getTotalPage() + "[split]" + "e"
				+ "[split]");
	} else {
		this.write(response, p.getPage().getTotalRecord() + "[split]"
				+ p.getPage().getTotalPage() + "[split]");
	}
